#import smtplib for our actual email sending function

import smtplib

#helper email modules
from email.mime.text import MIMEText
from email.mime.multipart import MIMEMultipart
from email.mime.base import MIMEBase
from email import encoders

#sender email address
email_user = "Your email"
email_password = "Your Password"

# List of users to whom email is to be sent
email_send = ["oluwapelumiabiodun45@gmail.com","milikiadbay@gmail.com"]

subject = "Testing My Code"

msg = MIMEMultipart()
msg['From'] = email_user

#converting list of recipients into comma separated string
msg['To'] = ", ".join(email_send)

msg['Subject'] = subject

body = """<html>
            <body>
              <p><b>Hello, It me Pelumi </b></p>
              <p><pre>
              I am reaching out to you to check if my Python Mail Broadcaster is working fine,
              and as well to tell you one mad thing that happend today.
                  
              I was given this graphics to finish within two Hours by Mr Wale.
              </pre></p>
              <img src="data:image/jpeg;base64," alt="Geep Image">
            </body>
          </html>
       """
msg.attach(MIMEText(body, 'html'))

text = msg.as_string()
server = smtplib.SMTP('smtp.gmail.com', 587)
server.starttls()
server.login(email_user, email_password)
server.sendmail(email_user, email_send, text)
server.quit()
